<?php declare(strict_types=1);

namespace Hardcastle\XRPL_PHP\Test\Core\RippleBinaryCodec\Types;

use PHPUnit\Framework\TestCase;
use Hardcastle\XRPL_PHP\Core\RippleBinaryCodec\Types\PathSet;

final class PathSetTest extends TestCase
{
    private array $json = [
        [
            [
                "account" => "r9hEDb4xBGRfBCcX3E4FirDWQBAYtpxC8K",
                "currency" => "BTC",
                "issuer" => "r9hEDb4xBGRfBCcX3E4FirDWQBAYtpxC8K",
            ],
            [
                "account" => "rM1oqKtfh1zgjdAgbFmaRm3btfGBX25xVo",
                "currency" => "BTC",
                "issuer" => "rM1oqKtfh1zgjdAgbFmaRm3btfGBX25xVo",
            ],
            [
                "account" => "rvYAfWj5gh67oV6fW32ZzP3Aw4Eubs59B",
                "currency" => "BTC",
                "issuer" => "rvYAfWj5gh67oV6fW32ZzP3Aw4Eubs59B",
            ],
            [
                "currency" => "USD",
                "issuer" => "rvYAfWj5gh67oV6fW32ZzP3Aw4Eubs59B",
            ],
        ],
        [
            [
                "account" => "r9hEDb4xBGRfBCcX3E4FirDWQBAYtpxC8K",
                "currency" => "BTC",
                "issuer" => "r9hEDb4xBGRfBCcX3E4FirDWQBAYtpxC8K",
            ],
            [
                "account" => "rM1oqKtfh1zgjdAgbFmaRm3btfGBX25xVo",
                "currency" => "BTC",
                "issuer" => "rM1oqKtfh1zgjdAgbFmaRm3btfGBX25xVo",
            ],
            [
                "account" => "rpvfJ4mR6QQAeogpXEKnuyGBx8mYCSnYZi",
                "currency" => "BTC",
                "issuer" => "rpvfJ4mR6QQAeogpXEKnuyGBx8mYCSnYZi",
            ],
            [
                "currency" => "USD",
                "issuer" => "rvYAfWj5gh67oV6fW32ZzP3Aw4Eubs59B",
            ],
        ],
        [
            [
                "account" => "r9hEDb4xBGRfBCcX3E4FirDWQBAYtpxC8K",
                "currency" => "BTC",
                "issuer" => "r9hEDb4xBGRfBCcX3E4FirDWQBAYtpxC8K",
            ],
            [
                "account" => "r3AWbdp2jQLXLywJypdoNwVSvr81xs3uhn",
                "currency" => "BTC",
                "issuer" => "r3AWbdp2jQLXLywJypdoNwVSvr81xs3uhn",
            ],
            ["currency" => "XRP"],
            [
                "currency" => "USD",
                "issuer" => "rvYAfWj5gh67oV6fW32ZzP3Aw4Eubs59B",
            ]
        ]
    ];

    private string $hex = "";

    public function testDecode(): void
    {
        $this->assertEquals(
            $this->json,
            PathSet::fromHex($this->hex)->toJson()
        );
    }

    public function testEncode(): void
    {
        $serializedJson = json_encode($this->json);
        $this->assertEquals(
            $this->hex,
            PathSet::fromJson($serializedJson)->toHex()
        );
    }
}